void render_bongocat(void) {
    if (timer_elapsed32(bongocat_anim_timer) > BONGOCAT_ANIM_FRAME_DURATION) {
        bongocat_anim_timer = timer_read32();

        // Render the bongocat animation
        bongocat_current_frame = (bongocat_current_frame + 1) % BONGOCAT_ANIM_FRAMES;
        oled_write_raw_P(bongocat_taps[bongocat_current_frame], BONGOCAT_ANIM_SIZE);
    }
}
